function overlay(){const checkBox=document.querySelector('#checkBox');const lightbox=document.querySelector('.lightbox');const overlay=document.querySelector('.overlay');const body=document.querySelector('body');if(checkBox.checked==true){lightbox.removeAttribute('onclick','setImage()');body.removeAttribute('onkeydown','setImage()');overlay.style.display='block'}else{lightbox.setAttribute('onclick','setImage()');body.setAttribute('onkeydown','setImage()');overlay.style.display='none'}}